import Button from '@material-ui/core/Button';
import { useInterval as setIntervalSpeed } from "./useInterval"; //change
import React, { useState, useRef, useEffect } from "react"; //change

//program variables

const boardGameSize = [window.innerWidth - 600, window.innerHeight - 200];
let totalScore = 0;
const snakeCoordinates = [[11, 10]];
//last key that was pushed in the game.
//this helps avoid key collisions
//Please refer to snakeKeyMovement method
let lastArrowPushed = 0;
let appleCoordinates = [Math.floor(Math.random() * 5), Math.floor(Math.random() * 5)];
const resizing = 34; //change
let snakeSpeed = 220;
const snakeDirectionList = {  //change
  38: [0, -1], // up  //change comment`
  40: [0, 1], // down  //change comment
  37: [-1, 0], // left  //change comment
  39: [1, 0] // right //change comment
};



//Creates a new target rectangle for the snake to eat upon collision
function createNewTargetShape(snakeEatsTargetShape, newTargetShape, arrayCopy, createNewTargetShape) {
  while (snakeEatsTargetShape(newTargetShape, arrayCopy)) {
    newTargetShape = createNewTargetShape();
  }
  return newTargetShape;
}

const App = () => { //change
  const [youLose, setYouLose] = useState(false);

  let canvas = useRef();
  let [snakeShapes, startingSnakePosition] = useState(snakeCoordinates); //change
  let [targetShape, targetShapeLocation] = useState(appleCoordinates); //change
  let [snakeDirections, changeSnakeDirection] = useState([0, -1]);
  let [speed, setSpeed] = useState(null); //change


  setGameController(snakeController, speed);

  const endGame = () => { //change
    setSpeed(null); //change
    setYouLose(true); //change
    alert("Nice Job! You ate " + totalScore + " apples!");
  };

    const snakeKeyMovement = ({keyCode}) => {
      console.log("snake is moving");
      console.log(keyCode);
      if(keyCode === 38) {
        console.log("Up ARROW pushed");
        if(!(lastArrowPushed === 40)) {
          console.log("ALL IS GOOD")
          lastArrowPushed = 38;
          changeSnakeDirection(snakeDirectionList[38]);
        }
      } else if(keyCode ===  39) {
        console.log("Right ARROW pushed");
        if(!(lastArrowPushed === 37)) {
          console.log("ALL IS GOOD");
          lastArrowPushed = 39;
          changeSnakeDirection(snakeDirectionList[39]);
        }
      } else if(keyCode === 40) {
        console.log("Down ARRAOW pushed");
        if(!(lastArrowPushed === 38)) {
          console.log("ALL IS GOOD");
          lastArrowPushed = 40;
          changeSnakeDirection(snakeDirectionList[40]);
        }
      } else if(keyCode === 37) {
        console.log("Left ARROW pushed");
        if(!(lastArrowPushed === 39)) {
          console.log("All is good");
          lastArrowPushed = 37;
          changeSnakeDirection(snakeDirectionList[37]);
        }
      }
    }

  const playSnakeGame = () => {
    //adjust snake speed here
    snakeSpeed = 180;
    appleCoordinates = [Math.floor(Math.random() * 5), Math.floor(Math.random() * 5)];
    totalScore = 0;
    targetShapeLocation(appleCoordinates);
    startingSnakePosition(snakeCoordinates);

    changeSnakeDirection([0, -1]); //change 
    setSpeed(snakeSpeed); //change 
    setYouLose(false); //change 
  };


  const createNewTargetShape = () => //change
    targetShape.map((_a, i) => Math.floor(Math.random() * (boardGameSize[i] / resizing))); //change

  const snakeEatsTargetShape = (piece, snk = snakeShapes) => { //change
    if ( //change
      piece[0] * resizing >= boardGameSize[0] || //change
      piece[0] < 0 || //change
      piece[1] * resizing >= boardGameSize[1] || //change
      piece[1] < 0 //change
    )
      return true; //change

    for (const segment of snk) { //change
      if (piece[0] === segment[0] && piece[1] === segment[1]) return true; //change
    }
    return false; //change
  };

  const snakeEatsApple = arrayCopy => { 
    if ((targetShape[0] === arrayCopy[0][0]) && (targetShape[1] === arrayCopy[0][1])) {
      //creates a new target shape for the snake
      let newTargetShape = createNewTargetShape();
      newTargetShape = createNewTargetShape(snakeEatsTargetShape, newTargetShape, arrayCopy, createNewTargetShape);
      targetShapeLocation(newTargetShape);
      if(snakeSpeed >= 70) 
      {
        snakeSpeed = snakeSpeed - 10;
        setSpeed(snakeSpeed);
      }
      totalScore += 1;
      return true;
    }
    return false;
  };


  const snakeController = () => {//change 
    const snakeCopy = JSON.parse(JSON.stringify(snakeShapes));//change 
    const newSnakeHead = [snakeCopy[0][0] + snakeDirections[0], snakeCopy[0][1] + snakeDirections[1]];//change 
    snakeCopy.unshift(newSnakeHead);//change 
    if (snakeEatsTargetShape(newSnakeHead)) endGame();//change 
    if (!snakeEatsApple(snakeCopy)) snakeCopy.pop();//change 
    startingSnakePosition(snakeCopy);//change 
  };



  useEffect(() => { //change 
    const shapes = canvas.current.getContext("2d");//change 
    shapes.setTransform(resizing, 0, 0, resizing, 0, 0); //change 
    let snakeGradient = shapes.createLinearGradient(255, 50, 50, 200);
    snakeGradient.addColorStop(0, "brown");
    snakeGradient.addColorStop(1, "black");
    shapes.clearRect(0, 0, window.innerWidth, window.innerHeight); //change 

    snakeShapes.forEach(([x, y]) => {
      shapes.fillStyle = snakeGradient;
      shapes.fillRect(x, y, .91, .91);
    });
    shapes.fillStyle = "red"; //change 
    shapes.fillRect(targetShape[0], targetShape[1], 1, 1);  //change 
  }, [snakeShapes, targetShape, youLose]); //change 

  return ( //change 
    <div onKeyDown={e => snakeKeyMovement(e)} style={{ border: "none", outline: "none"  }}  > 

      <Button variant="contained" color="secondary" onClick={playSnakeGame}>
        Play
      </Button>

      <center>

        <canvas
          style={{ border: "1px solid black" }}
          ref={canvas}
          width={`${boardGameSize[0]}px`}
          height={`${boardGameSize[1]}px`}
        />

      </center>

    </div>
  ); //change 
};

export default App;

function setGameController(snakeController, speed) {
  setIntervalSpeed(() => snakeController(), speed);
}
