import React from "react";

import {
    CANVAS_SIZE,
    SNAKE_START,
    APPLE_START,
    SCALE,
    SPEED,
    DIRECTIONS
} from './constants';

import { useInterval } from './useInterval';
import { useRef, useState, useEffect } from "react";

const App = () => {
    const canvasRef = useRef();
    const [snake, setSnake] = useState(SNAKE_START);
    const [apple, setApple] = useState(APPLE_START);
    //set direction
    const [dir, setDir] = useState([0, -1]);
    const [ speed, setSpeed] = useState(1000);
    const [gameOver, setGameOver] = useState(false);



    const startGame = () => {

    }


    const endGame = () => {

    }

    //takes keycodes from constant array
    const moveSnake = ({keyCode}) => keyCode >= 37 && keyCode <= 40 && setDir(DIRECTIONS[keyCode]);




    const createApple = () => {

    }


    const checkCollision = () => {

    }

    const checkAppleCollision = () => {


    }

    const gameLoop = () => {
        const snakeCopy = JSON.parse(JSON.stringify(snake));
        const newSnakeHead = [snakeCopy[0][0] + dir[0], snakeCopy[0][1] + dir[1]];
        //unshift method adds new items to the begining of the array
        //each new head that is generated gets added to the begining of the snake
        snakeCopy.unshift(newSnakeHead);
        //this removes the last shape element in the array to help the snake "crawl" across the screen
        snakeCopy.pop();
    }


    //
    useEffect(() => {
        const context = canvasRef.current.getContext("2d");
        context.setTransform(SCALE, 0, 0, SCALE, 0, 0);
        context.clearRect(0, 0, CANVAS_SIZE[0], CANVAS_SIZE[1]);
        context.fillStyle = "pink";
        snake.forEach(([x, y]) => context.fillRect(x, y, 1, 1));
        context.fillStyle = "lightblue";
        context.fillRect(apple[0], apple[1], 1, 1);

    }, [snake, apple, gameOver]);

    useInterval(() => gameLoop(), speed);


    return (
        <div role="button" tabIndex="0" onKeyDown={e => moveSnake(e)}>
            <canvas style={{ border: "1px solid black" }}
                ref={canvasRef}
                width={`${CANVAS_SIZE[0]}px`}
                height={`${CANVAS_SIZE[1]}px`}
                />
            {gameOver && <div>Game Over</div>}
            <button onClick={startGame}>Start Game</button>
        </div>
    )

}

export default App;
